import type { Block } from "payload";
import {
	BLOCK_GROUP_FOOTERS,
	BLOCK_SLUG_FOOTER_1,
} from "@/registry/default/lib/constants/blocks";
import socialLinksField from "@/registry/default/lib/fields/socialLinks/config";
import mediaField from "@/registry/default/lib/fields/media/config";

export const Footer_1_Block: Block = {
	slug: BLOCK_SLUG_FOOTER_1,
	interfaceName: "Footer_1_Block",
	labels: {
		singular: "Footer 1",
		plural: "Footer 1's",
	},
	admin: {
		group: BLOCK_GROUP_FOOTERS,
	},
	fields: [
		mediaField({
			name: "brandLogo",
			label: "Brand Logo",
		}),
		{
			name: "copyright",
			type: "text",
			label: "Copyright",
			defaultValue: "UIFoundry, All rights reserved",
		},
		{
			name: "links",
			type: "array",
			labels: {
				singular: "Link",
				plural: "Links",
			},
			fields: [
				{
					name: "label",
					type: "text",
					label: "Label",
					required: true,
				},
				{
					name: "href",
					type: "text",
					label: "Link (Href)",
					required: true,
				},
			],
			defaultValue: [
				{
					label: "Features",
					href: "/",
				},
				{
					label: "Solution",
					href: "/",
				},
				{
					label: "Customer",
					href: "/",
				},
				{
					label: "Pricing",
					href: "/",
				},
				{
					label: "Help",
					href: "/",
				},
				{
					label: "About",
					href: "/",
				},
			],
		},
		socialLinksField({
			defaultValue: [
				{
					href: "https://x.com",
					icon: "X (formerly Twitter)",
				},
				{
					href: "https://linkedin.com",
					icon: "LinkedIn",
				},
				{
					href: "https://facebook.com",
					icon: "Facebook",
				},
				{
					href: "https://threads.com",
					icon: "Threads",
				},
				{
					href: "https://instagram.com",
					icon: "Instagram",
				},
				{
					href: "https://tiktok.com",
					icon: "TikTok",
				},
			],
		}),
	],
};

"use client";

import Image from "next/image";
import Link from "next/link";
import { type ComponentPropsWithRef } from "react";
import type { Footer_1_Block, Media } from "~/payload-types";
import SocialIcon from "@/registry/default/lib/fields/socialLinks";

export default function Footer_1({
	brandLogo,
	links,
	socialLinks,
	copyright,
	...divProps
}: Footer_1_Block & ComponentPropsWithRef<"div">) {
	return (
		<footer {...divProps}>
			<div className="mx-auto max-w-5xl px-6 py-8 md:py-16">
				<Link href="/" aria-label="go home" className="mx-auto block size-fit">
					{brandLogo && (
						<Image
							src={(brandLogo as Media).url!}
							alt={(brandLogo as Media).alt}
							width={200}
							height={150}
						/>
					)}
				</Link>

				<div className="flex flex-wrap justify-center gap-6 text-sm">
					{(links ?? []).map((link, index) => (
						<Link
							key={index}
							href={link.href}
							className="text-muted-foreground hover:text-primary block duration-150"
						>
							<span>{link.label}</span>
						</Link>
					))}
				</div>
				<div className="my-8 flex flex-wrap justify-center gap-6 text-sm">
					{socialLinks.map((icon, index) => (
						<SocialIcon icon={icon.icon} href={icon.href} key={`${index}`} />
					))}
				</div>
				<span className="text-muted-foreground block text-center text-sm">
					{" "}
					Â© {new Date().getFullYear()} {copyright}
				</span>
			</div>
		</footer>
	);
}
