import type { Block } from "payload";
import {
	BLOCK_GROUP_FOOTERS,
	BLOCK_SLUG_FOOTER_4,
} from "@/registry/default/lib/constants/blocks";

export const Footer_4_Block: Block = {
	slug: BLOCK_SLUG_FOOTER_4,
	interfaceName: "Footer_4_Block",
	labels: {
		singular: "Footer 4",
		plural: "Footer 4's",
	},
	admin: {
		group: BLOCK_GROUP_FOOTERS,
	},
	fields: [
		{
			name: "copyright",
			type: "text",
			label: "Copyright",
			defaultValue: "UIFoundry, All rights reserved",
		},
		{
			name: "links",
			type: "array",
			labels: {
				singular: "Link",
				plural: "Links",
			},
			fields: [
				{
					name: "label",
					type: "text",
					label: "Label",
					required: true,
				},
				{
					name: "href",
					type: "text",
					label: "Link (Href)",
					required: true,
				},
			],
			defaultValue: [
				{
					label: "Features",
					href: "/",
				},
				{
					label: "Solution",
					href: "/",
				},
				{
					label: "Customer",
					href: "/",
				},
				{
					label: "Pricing",
					href: "/",
				},
				{
					label: "Help",
					href: "/",
				},
				{
					label: "About",
					href: "/",
				},
			],
		},
	],
};

import Link from "next/link";
import { type ComponentPropsWithRef } from "react";
import type { Footer_4_Block } from "~/payload-types";
import { cn } from "@/registry/default/utils";

export * from "./config";

export default function Footer_4({
	links,
	copyright,
	className,
	...divProps
}: Footer_4_Block & ComponentPropsWithRef<"div">) {
	return (
		<footer
			className={cn("border-b bg-white py-12 dark:bg-transparent", className)}
			{...divProps}
		>
			<div className="mx-auto max-w-5xl px-6">
				<div className="flex flex-wrap justify-between gap-6">
					<span className="text-muted-foreground order-last block text-center text-sm md:order-first">
						Â© {new Date().getFullYear()} {copyright}
					</span>
					<div className="order-first flex flex-wrap justify-center gap-6 text-sm md:order-last">
						{links?.map((link, index) => (
							<Link
								key={index}
								href={link.href}
								className="text-muted-foreground hover:text-primary block duration-150"
							>
								<span>{link.label}</span>
							</Link>
						))}
					</div>
				</div>
			</div>
		</footer>
	);
}
