import type { Block } from "payload";
import {
	BLOCK_GROUP_HEADERS,
	BLOCK_SLUG_HEADER_MENU_ITEMS,
} from "@/registry/default/lib/constants/blocks";
import { FLEX_ALIGNMENT } from "@/registry/default/lib/constants";
import selectEnumField from "@/registry/default/lib/fields/selectEnumField/config";

export const HeaderMenuItemsBlock: Block = {
	slug: BLOCK_SLUG_HEADER_MENU_ITEMS,
	interfaceName: "HeaderMenuItemsBlock",
	labels: {
		singular: "Menu Items",
		plural: "Menu Items'",
	},
	admin: {
		group: BLOCK_GROUP_HEADERS,
	},
	fields: [
		selectEnumField(FLEX_ALIGNMENT, {
			name: "alignment",
			label: "Header Alignment",
			defaultValue: FLEX_ALIGNMENT.center,
		}),
		{
			name: "menuItems",
			labels: {
				singular: "Menu Item",
				plural: "Menu Items",
			},
			type: "array",
			required: true,
			defaultValue: [
				{
					label: "Home",
					href: "/",
				},
				{
					label: "Products",
					href: "/products",
				},
				{
					label: "Services",
					href: "/services",
				},
				{
					label: "Contact",
					href: "/contact",
				},
			],
			fields: [
				{
					name: "label",
					label: "Label",
					type: "text",
					required: true,
				},
				{
					name: "href",
					label: "Href",
					type: "text",
					required: true,
					admin: {
						placeholder: "/features | #features",
					},
				},
			],
		},
	],
};

import Link from "next/link";
import type { HeaderMenuItemsBlock } from "~/payload-types";
import { cn } from "@/registry/default/utils";

export default function MenuItems({
	isMobile = false,
	menuItems,
}: { isMobile: boolean } & HeaderMenuItemsBlock) {
	return (
		<div className={cn("hidden lg:block", isMobile === true && "block")}>
			<ul
				className={cn(
					"flex gap-8 text-sm",
					isMobile === true && "flex-col gap-4 text-center",
				)}
			>
				{menuItems.map((item, index) => (
					<li key={index}>
						<Link
							href={item.href}
							className="text-muted-foreground hover:text-primary block duration-300"
						>
							<span>{item.label}</span>
						</Link>
					</li>
				))}
			</ul>
		</div>
	);
}
