"use client";

import Link from "next/link";
import { Button } from "@/registry/ui/button";
import type { Hero_2_Block } from "~/payload-types";
import MediaField from "@/registry/default/lib/fields/media/index";
import { cn } from "@/registry/default/utils";

export default function Hero2Section(props: Hero_2_Block) {
	return (
		<section className="relative overflow-hidden">
			{/* Content Container */}
			<div className="py-24 md:pb-32 lg:pt-44 lg:pb-56">
				<div className="relative mx-auto flex max-w-7xl flex-col px-6 lg:flex-row lg:items-center lg:px-12">
					{/* Left Content Column */}
					<div className="mx-auto max-w-lg text-center lg:mx-0 lg:w-1/2 lg:max-w-full lg:text-left">
						{/* Main Heading */}
						<h1 className="text-5xl font-semibold text-balance md:text-6xl xl:text-7xl">
							{props.header}
						</h1>

						{/* Subheader */}
						{props.subheader && (
							<p className="text-muted-foreground mt-6 text-lg text-pretty">
								{props.subheader}
							</p>
						)}

						{/* CTA Buttons */}
						<div className="mt-8 flex flex-col items-center justify-center gap-3 sm:flex-row lg:justify-start">
							{/* Primary CTA */}
							<Button
								asChild
								size="lg"
								className="inline-flex h-11 items-center justify-center gap-2 rounded-lg px-6 text-base font-medium whitespace-nowrap shadow-sm"
							>
								<Link href={props.primaryCtaHref}>{props.primaryCtaLabel}</Link>
							</Button>

							{/* Secondary CTA */}
							<Button
								asChild
								variant="outline"
								size="lg"
								className="inline-flex h-11 items-center justify-center gap-2 rounded-lg px-6 text-base font-medium whitespace-nowrap"
							>
								<Link href={props.secondaryCtaHref}>
									{props.secondaryCtaLabel}
								</Link>
							</Button>
						</div>
					</div>

					{/* Right Image Column */}
					{props.media && (
						<div className="relative mt-12 lg:mt-0 lg:w-1/2 lg:pl-12">
							<div className="relative aspect-[4/3] w-full overflow-hidden rounded-2xl lg:aspect-square">
								<MediaField
									media={props.media}
									width="800"
									height="800"
									className={cn(
										"size-full object-cover object-center",
										"rounded-2xl shadow-2xl",
									)}
								/>
							</div>
							{/* Decorative background gradient */}
							<div
								aria-hidden="true"
								className="from-primary/20 via-primary/10 absolute inset-0 -z-10 translate-x-8 translate-y-8 rounded-2xl bg-gradient-to-br to-transparent blur-3xl"
							/>
						</div>
					)}
				</div>
			</div>

			{/* Background gradient */}
			<div
				aria-hidden="true"
				className="absolute inset-x-0 top-0 -z-20 h-[800px] overflow-hidden"
			>
				<div className="from-primary/5 absolute inset-0 bg-gradient-to-b via-transparent to-transparent" />
			</div>
		</section>
	);
}

import {
	BLOCK_GROUP_HERO,
	BLOCK_SLUG_HERO_2,
} from "@/registry/default/lib/constants/blocks";
import type { Block } from "payload";
import headerField from "@/registry/default/lib/fields/header/config";
import subHeaderField from "@/registry/default/lib/fields/subheader/config";
import mediaField from "@/registry/default/lib/fields/media/config";

export const Hero_2_Block: Block = {
	slug: BLOCK_SLUG_HERO_2,
	labels: {
		singular: "Hero 2",
		plural: "Hero 2's",
	},
	admin: {
		group: BLOCK_GROUP_HERO,
	},
	interfaceName: "Hero_2_Block",
	fields: [
		headerField({
			defaultValue: "Ship 10x Faster with NS",
		}),
		subHeaderField({
			defaultValue:
				"Highly customizable components for building modern websites and applications that look and feel the way you mean it.",
		}),
		{
			label: "Primary Call To Action",
			type: "collapsible",
			admin: {
				initCollapsed: false,
			},
			fields: [
				{
					name: "primaryCtaLabel",
					label: "Primary Call To Action: Label",
					type: "text",
					required: true,
					defaultValue: "Start Building",
				},
				{
					name: "primaryCtaHref",
					label: "Primary Call To Action: Link (href)",
					type: "text",
					required: true,
					defaultValue: "/get-started",
				},
			],
		},
		{
			label: "Secondary Call To Action",
			type: "collapsible",
			admin: {
				initCollapsed: false,
			},
			fields: [
				{
					name: "secondaryCtaLabel",
					label: "Secondary Call To Action: Label",
					type: "text",
					required: true,
					defaultValue: "Request a demo",
				},
				{
					name: "secondaryCtaHref",
					label: "Secondary Call To Action: Link (href)",
					type: "text",
					required: true,
					defaultValue: "/demo",
				},
			],
		},
		mediaField({
			label: "Background Image",
			admin: {
				description:
					"Upload background image (light/dark variants). Displays as a floating image on the right side.",
			},
		}),
	],
};
