import type { Hero_2_Block } from "~/payload-types";

export * from "./config";

export default function Hero_2(props: Hero_2_Block) {
  return (
    <div className="grid h-[75vh] w-full grid-cols-2 bg-red-50 p-4">
      <div className="flex h-full flex-col items-start justify-center gap-2 bg-blue-200/30">
        <h1 className="clamp-[text,4xl,6xl]">{props.header}</h1>
        <p className="clamp-[text,md,xl]">{props.subheader}</p>
      </div>
      <div className="col-start-2 flex items-center justify-center">
        image goes here
      </div>
    </div>
  );
}

import {
  BLOCK_GROUP_HERO,
  BLOCK_SLUG_HERO_2,
} from "@/registry/default/lib/constants/blocks";
import type { Block } from "payload";
import headerField from "@/registry/default/lib/fields/header/config";
import subHeaderField from "@/registry/default/lib/fields/subheader/config";
import mediaField from "@/registry/default/lib/fields/media/config";
import callToActionPair from "@/registry/default/lib/fields/cta-pair/config";

export const Hero_2_Block: Block = {
  slug: BLOCK_SLUG_HERO_2,
  labels: {
    singular: "Hero 2",
    plural: "Hero 2's",
  },
  admin: {
    group: BLOCK_GROUP_HERO,
  },
  interfaceName: "Hero_2_Block",
  fields: [
    headerField(),
    subHeaderField(),
    mediaField({ name: "background", label: "Background Media" }),
    callToActionPair(),
  ],
};
